import{_ as e,c as t,o as l,aN as r}from"./chunks/framework.DXgs1r1w.js";const u=JSON.parse('{"title":"文件传输协议","description":"","frontmatter":{"date":"2024-10-31T15:42:09.000Z","title":"文件传输协议","permalink":"/pages/c0c719","categories":["Protocol"]},"headers":[],"relativePath":"Protocol/index.md","filePath":"Protocol/index.md","lastUpdated":1765964436000}'),i={name:"Protocol/index.md"};function o(d,a,h,n,s,c){return l(),t("div",null,[...a[0]||(a[0]=[r('<h1 id="文件传输协议" tabindex="-1">文件传输协议 <a class="header-anchor" href="#文件传输协议" aria-label="Permalink to &quot;文件传输协议&quot;">​</a></h1><p>在系统设计中，文件的传输与共享通常依赖标准化协议。不同协议在安全性、性能、使用场景上差异明显，合理选型可以显著降低系统复杂度与运维成本。</p><p>本章节主要介绍 <code>FTP、SFTP、S3、SMB</code> 四种常见方案。</p><hr><h2 id="ftp-file-transfer-protocol" tabindex="-1">FTP（File Transfer Protocol） <a class="header-anchor" href="#ftp-file-transfer-protocol" aria-label="Permalink to &quot;FTP（File Transfer Protocol）&quot;">​</a></h2><h3 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h3><p>FTP 是一种传统的文件传输协议，采用客户端 / 服务器模型，通过控制连接发送命令，通过数据连接传输文件内容。</p><h3 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h3><p>明文传输用户名、密码和数据<br> 支持主动模式与被动模式<br> 协议历史悠久，兼容性强</p><h3 id="优缺点" tabindex="-1">优缺点 <a class="header-anchor" href="#优缺点" aria-label="Permalink to &quot;优缺点&quot;">​</a></h3><p>优点</p><ul><li>实现简单</li><li>工具和客户端丰富</li></ul><p>缺点</p><ul><li>无加密，安全性差</li><li>对防火墙和 NAT 不友好</li><li>不适合公网和敏感数据场景</li></ul><h3 id="适用场景" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景" aria-label="Permalink to &quot;适用场景&quot;">​</a></h3><ul><li>内网测试环境</li><li>历史系统兼容需求</li></ul><h2 id="sftp-ssh-file-transfer-protocol" tabindex="-1">SFTP（SSH File Transfer Protocol） <a class="header-anchor" href="#sftp-ssh-file-transfer-protocol" aria-label="Permalink to &quot;SFTP（SSH File Transfer Protocol）&quot;">​</a></h2><h3 id="简介-1" tabindex="-1">简介 <a class="header-anchor" href="#简介-1" aria-label="Permalink to &quot;简介&quot;">​</a></h3><p>SFTP 是基于 SSH 协议的文件传输子协议，与 FTP 在实现上完全不同，仅名称相似。</p><h3 id="特点-1" tabindex="-1">特点 <a class="header-anchor" href="#特点-1" aria-label="Permalink to &quot;特点&quot;">​</a></h3><p>单一 TCP 连接（默认 22 端口） 全程加密（认证 + 数据） 支持权限控制与审计</p><h3 id="优缺点-1" tabindex="-1">优缺点 <a class="header-anchor" href="#优缺点-1" aria-label="Permalink to &quot;优缺点&quot;">​</a></h3><p>优点</p><ul><li>安全性高</li><li>穿透防火墙能力强</li><li>运维和配置成本低</li></ul><p>缺点</p><ul><li>性能略低于明文协议</li><li>依赖 SSH 服务</li></ul><h3 id="适用场景-1" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景-1" aria-label="Permalink to &quot;适用场景&quot;">​</a></h3><ul><li>安全文件上传 / 下载</li><li>自动化任务、运维系统</li><li>企业内外网文件交换</li><li>工程实践中的通用首选方案</li></ul><h2 id="s3-object-storage-s3-api" tabindex="-1">S3（Object Storage / S3 API） <a class="header-anchor" href="#s3-object-storage-s3-api" aria-label="Permalink to &quot;S3（Object Storage / S3 API）&quot;">​</a></h2><h3 id="简介-2" tabindex="-1">简介 <a class="header-anchor" href="#简介-2" aria-label="Permalink to &quot;简介&quot;">​</a></h3><p>S3 是对象存储模型的代表，通过 HTTP/HTTPS API 访问数据，不是传统意义上的文件系统。</p><h3 id="核心概念" tabindex="-1">核心概念 <a class="header-anchor" href="#核心概念" aria-label="Permalink to &quot;核心概念&quot;">​</a></h3><p>Bucket：逻辑容器<br> Object：对象（Key + Data + Metadata）<br> Prefix：模拟目录结构</p><h3 id="特点-2" tabindex="-1">特点 <a class="header-anchor" href="#特点-2" aria-label="Permalink to &quot;特点&quot;">​</a></h3><p>无目录、无文件锁概念<br> 支持分片上传、断点续传<br> 高可用、高扩展</p><h3 id="优缺点-2" tabindex="-1">优缺点 <a class="header-anchor" href="#优缺点-2" aria-label="Permalink to &quot;优缺点&quot;">​</a></h3><p>优点</p><ul><li>适合海量数据存储</li><li>易于横向扩展</li><li>云原生友好</li></ul><p>缺点</p><ul><li>延迟相对较高</li><li>不适合频繁小文件随机读写</li><li>不符合 POSIX 文件语义</li></ul><h3 id="适用场景-2" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景-2" aria-label="Permalink to &quot;适用场景&quot;">​</a></h3><ul><li>对象存储</li><li>备份、归档、日志</li><li>分布式系统、云服</li></ul><h2 id="smb-server-message-block" tabindex="-1">SMB（Server Message Block） <a class="header-anchor" href="#smb-server-message-block" aria-label="Permalink to &quot;SMB（Server Message Block）&quot;">​</a></h2><h3 id="简介-3" tabindex="-1">简介 <a class="header-anchor" href="#简介-3" aria-label="Permalink to &quot;简介&quot;">​</a></h3><p>SMB 是一种网络文件系统协议，目标是让远程文件像本地文件一样被访问。</p><h3 id="特点-3" tabindex="-1">特点 <a class="header-anchor" href="#特点-3" aria-label="Permalink to &quot;特点&quot;">​</a></h3><p>支持目录操作、文件锁、权限继承</p><p>与操作系统深度集成</p><p>Windows 环境下使用最广泛</p><h3 id="优缺点-3" tabindex="-1">优缺点 <a class="header-anchor" href="#优缺点-3" aria-label="Permalink to &quot;优缺点&quot;">​</a></h3><p>优点</p><ul><li><p>使用透明，应用无感知</p></li><li><p>适合共享目录和协作办公 缺点</p></li><li><p>对网络延迟敏感</p></li><li><p>跨公网性能较差</p></li><li><p>配置和安全策略复杂</p></li></ul><h3 id="适用场景-3" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景-3" aria-label="Permalink to &quot;适用场景&quot;">​</a></h3><ul><li>局域网文件共享</li><li>企业内部文件服务器</li><li>NAS / 文件集中管理</li></ul><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><table tabindex="0"><thead><tr><th>协议</th><th>模型</th><th>是否加密</th><th>语义</th><th>典型场景</th></tr></thead><tbody><tr><td>FTP</td><td>文件传输</td><td>否</td><td>文件</td><td>遗留系统</td></tr><tr><td>SFTP</td><td>文件传输</td><td>是</td><td>文件</td><td>安全传输</td></tr><tr><td>S3</td><td>对象存储</td><td>是</td><td>对象</td><td>云与海量数据</td></tr><tr><td>SMB</td><td>网络文件系统</td><td>可选</td><td>文件系统</td><td>内网共享</td></tr></tbody></table>',56)])])}const b=e(i,[["render",o]]);export{u as __pageData,b as default};
